    align-items: center;
    padding: 0.75rem 0;
    border-bottom: 1px solid #e5e7eb;
  }

  .item-details {
    flex-grow: 1;
  }

  .item-actions {
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }

  .quantity-button, .remove-button {
    padding: 0.25rem 0.5rem;
    background-color: #f3f4f6;
